'use strict';
let DOMParser = require('xmldom').DOMParser;
let fs = require('fs');
class XmlTreeService {
    static getXmlTreeHtml(xml) {
        let xdoc = new DOMParser().parseFromString(xml, 'text/xml');
        let fontColor = XmlTreeService._getRecommendedTextColor();
        let html = `
        <!DOCTYPE html>
        <html>
        <head>
            <title>XML Tree View</title>
            <style>
                *, html {
                    font-family: Verdana, Arial, Helvetica, sans-serif;
                }

                body, form, ul, li, p, h1, h2, h3, h4, h5 {
                    margin: 0;
                    padding: 0;
                }

                body {
                    color: ${fontColor};
                    margin: 0;
                }

                img {
                    border: none;
                }

                p {
                    font-size: 1em;
                    margin: 0 0 1em 0;
                }

                html {
                    font-size: 100%;
                    /* IE hack */
                }

                body {
                    font-size: 1em;
                    /* Sets base font size to 16px */
                }

                table {
                    font-size: 100%;
                    /* IE hack */
                }

                input, select, textarea, th, td {
                    font-size: 1em;
                }


                /* CSS Tree menu styles */

                ol.tree {
                    padding: 0 0 0 30px;
                    width: 300px;
                }

                li {
                    position: relative;
                    margin-left: -15px;
                    list-style: none;
                }

                li.xml {
                    margin-left: -1px !important;
                }

                li.xml a {
                    background: url(document.png) 0 0 no-repeat;
                    color: ${fontColor};
                    padding-left: 21px;
                    text-decoration: none;
                    display: block;
                }

                li.xml-attribute a {
                    background: url(data:image/png;base64,) 0 0 no-repeat;
                }

                li.xml-text a {
                    background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAQCAYAAAAf8/9hAAAAAXNSR0IArs4c6QAAAElJREFUOBFjYBhowIjsgDnqG1YB+aHIYphsxrCUm/6rYeJMMAa5NIoLkA2Zo74R6JL/IBcxpNwMwKmOYheMGsDAMBoGyElvoNgA5p8KF92MLqAAAAAASUVORK5CYII=) 0 0 no-repeat;
                }

                li input {
                    position: absolute;
                    left: 0;
                    margin-left: 0;
                    opacity: 0;
                    z-index: 2;
                    cursor: pointer;
                    height: 1em;
                    width: 1em;
                    top: 0;
                }

                li input + ol {
                    background: url(data:image/png;base64,) 40px 0 no-repeat;
                    margin: -0.938em 0 0 -44px;
                    /* 15px */
                    height: 1em;
                }

                li input + ol > li {
                    display: none;
                    margin-left: -14px !important;
                    padding-left: 1px;
                }

                li label {
                    color: ${fontColor};
                    background: url(data:image/png;base64,) 15px 1px no-repeat;
                    cursor: pointer;
                    display: block;
                    padding-left: 37px;
                }

                li input:checked + ol {
                    background: url(data:image/png;base64,) 40px 5px no-repeat;
                    margin: -1.25em 0 0 -44px;
                    /* 20px */
                    padding: 1.563em 0 0 80px;
                    height: auto;
                }

                li input:checked + ol > li {
                    display: block;
                    margin: 0 0 0.125em;
                    /* 2px */
                }

                li input:checked + ol > li:last-child {
                    margin: 0 0 0.063em;
                    /* 1px */
                }
            </style>
            <script>function noop(){};</script>
        </head>
        <body>
        `;
        html += `<ol class="tree">`;
        html += XmlTreeService._processXmlNode(xdoc.lastChild);
        html += `</ol>`;
        html +=
            `
        </body>
        </html>
        `;
        return html;
    }
    static _processXmlNode(node) {
        let html = '';
        if (node.childNodes) {
            html += `<li><label for="ID">${node.localName}</label><input type="checkbox" id="ID" /><ol>`;
        }
        if (node.attributes) {
            for (let i = 0; i < node.attributes.length; i++) {
                html += `<li class="xml xml-attribute"><a href="javascript:noop()">${node.attributes.item(i).localName} = '${node.attributes.item(i).value}'</a></li>`;
            }
        }
        if (!node.childNodes && node.textContent) {
            html += `<li class="xml xml-text"><a href="javascript:noop()">${node.textContent}</a></li>`;
        }
        if (node.childNodes) {
            for (let i = 0; i < node.childNodes.length; i++) {
                html += XmlTreeService._processXmlNode(node.childNodes.item(i));
            }
            html += `</ol></li>`;
        }
        return html;
    }
    static _getRecommendedTextColor() {
        let color = '#AAAAAA';
        let path = '';
        switch (process.platform) {
            case 'darwin':
                path = `${process.env.HOME}/Library/Application Support/Code/storage.json`;
                break;
            case 'win32':
                path = `${process.env.APPDATA}\\Code\\storage.json`;
                break;
            default:
                path = `${process.env.HOME}/.config/Code/storage.json`;
                break;
        }
        try {
            fs.accessSync(path);
            let json = fs.readFileSync(path, 'utf8');
            let storage = JSON.parse(json);
            color = (storage.theme.indexOf('vs-dark') > -1) ? '#FFFFFF' : '#000000';
        }
        catch (error) { }
        return color;
    }
}
exports.XmlTreeService = XmlTreeService;
